import React, { useState } from 'react';
import './Home.css';
import { Button, TextField, MenuItem, FormControl, InputLabel, Select } from '@material-ui/core';

const cities = ['Pune', 'Mumbai', 'Banglore'];
const states = ['Karnataka', 'UP', 'Punjab'];
const categories = ['Dancers', 'Singers']

export default function Home() {
    const [city, setCity] = useState('');
    const [state, setState] = useState('');

    const handleCity = (e) => {
        setCity(e.target.value)
    }

    const handleState = (e) => {
        setState(e.target.value)
    }
    console.log(city)

    return (
        <div className='home1'>
            <div className='paper'>
                <FormControl className='form-control'>
                    <InputLabel id="city-label">CITY</InputLabel>
                    <Select
                        labelId="city-label"
                        id="city-select"
                        value={city}
                        onChange={handleCity}
                    >
                        {cities.map((city) => (
                            <MenuItem key={city} value={city}>
                                {city}
                            </MenuItem>
                        ))}
                    </Select>
                    <InputLabel id="state-label">STATE</InputLabel>
                    <Select
                        labelId="state-label"
                        id="state-select"
                        value={state}
                        onChange={handleState}
                    >
                        {states.map((state) => (
                            <MenuItem key={state} value={state}>
                                {state}
                            </MenuItem>
                        ))}
                    </Select>
                    <InputLabel id="state-label">STATE</InputLabel>
                    <Select
                        labelId="state-label"
                        id="state-select"
                        value={state}
                        onChange={handleState}
                    >
                        {categories.map((state) => (
                            <MenuItem key={state} value={state}>
                                {state}
                            </MenuItem>
                        ))}
                    </Select>
                </FormControl>
            </div>
        </div>
    )
}
